const observerOptions={threshold:0.1};const observer=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add('active');}});},observerOptions);document.querySelectorAll('.reveal-node').forEach(node=>observer.observe(node));document.querySelectorAll('.faq-btn').forEach(btn=>{btn.addEventListener('click',()=>{const content=btn.nextElementSibling;const icon=btn.querySelector('i');const isOpen=content.style.maxHeight!=='0px'&&content.style.maxHeight!=='';document.querySelectorAll('.faq-content').forEach(el=>el.style.maxHeight='0px');document.querySelectorAll('.faq-btn i').forEach(el=>el.style.transform='rotate(0deg)');if(!isOpen){content.style.maxHeight=content.scrollHeight+'px';icon.style.transform='rotate(45deg)';}});});const apptRange=document.getElementById('apptRange');const priceRange=document.getElementById('priceRange');const apptVal=document.getElementById('apptVal');const priceVal=document.getElementById('priceVal');const roiTotal=document.getElementById('roiTotal');function updateROI(){const a=parseInt(apptRange.value);const p=parseInt(priceRange.value);apptVal.innerText=a;priceVal.innerText=`R${p.toLocaleString()}`;const monthly=Math.round((a*p*0.18*4.3)+(a*15*4.3));roiTotal.innerText=`R${monthly.toLocaleString()}`;}
apptRange.oninput=updateROI;priceRange.oninput=updateROI;updateROI();const menuBtn=document.getElementById('menu-toggle');const mobileMenu=document.getElementById('mobile-menu');menuBtn.addEventListener('click',()=>{const isOpen=mobileMenu.style.transform==='translateX(0%)';mobileMenu.style.transform=isOpen?'translateX(100%)':'translateX(0%)';menuBtn.innerHTML=isOpen?'<i class="fa-solid fa-bars-staggered"></i>':'<i class="fa-solid fa-xmark"></i>';});;