function reveal(){const reveals=document.querySelectorAll(".reveal");for(let i=0;i<reveals.length;i++){let windowHeight=window.innerHeight;let elementTop=reveals[i].getBoundingClientRect().top;let elementVisible=100;if(elementTop<windowHeight-elementVisible){reveals[i].classList.add("active");}}}
window.addEventListener("scroll",reveal);reveal();const apptRange=document.getElementById('apptRange');const priceRange=document.getElementById('priceRange');const apptVal=document.getElementById('apptVal');const priceVal=document.getElementById('priceVal');const roiTotal=document.getElementById('roiTotal');function updateROI(){const a=parseInt(apptRange.value);const p=parseInt(priceRange.value);apptVal.innerText=a;priceVal.innerText=`R${p.toLocaleString()}`;const monthly=Math.round((a*p*0.18*4.3)+(a*15*4.3));roiTotal.innerText=`R${monthly.toLocaleString()}`;}
if(apptRange){apptRange.oninput=updateROI;priceRange.oninput=updateROI;updateROI();};