const apptRange=document.getElementById('apptRange');const priceRange=document.getElementById('priceRange');const apptVal=document.getElementById('apptVal');const priceVal=document.getElementById('priceVal');const roiTotal=document.getElementById('roiTotal');function updateROI(){const a=parseInt(apptRange.value);const p=parseInt(priceRange.value);apptVal.innerText=a;priceVal.innerText=`R${p.toLocaleString()}`;const monthly=Math.round((a*p*0.18*4.3)+(a*20*4.3));roiTotal.innerText=`R${monthly.toLocaleString()}`;}
apptRange.oninput=updateROI;priceRange.oninput=updateROI;updateROI();document.querySelectorAll('.faq-button').forEach(button=>{button.addEventListener('click',()=>{const answer=button.nextElementSibling;const icon=button.querySelector('i');const isOpen=answer.style.maxHeight!=='0px'&&answer.style.maxHeight!=='';document.querySelectorAll('.faq-answer').forEach(el=>el.style.maxHeight='0px');document.querySelectorAll('.faq-button i').forEach(el=>el.style.transform='rotate(0deg)');if(!isOpen){answer.style.maxHeight=answer.scrollHeight+"px";icon.style.transform='rotate(45deg)';}});});const menuBtn=document.getElementById('menu-toggle');menuBtn.addEventListener('click',()=>{});;